<?php

namespace icy2003\php\tests\ihelpers;

use icy2003\php\I;
use icy2003\php\ihelpers\Base64;
use icy2003\php\ihelpers\File;

class Base64Test extends \Codeception\Test\Unit
{

    private function __getString()
    {
        return '';
    }

    public function testIsBase64()
    {
        parent::assertTrue(Base64::isBase64('aWN5MjAwMw=='));
        parent::assertFalse(Base64::isBase64('aWN5MjAwMw='));
    }

    public function testFromFile()
    {
        parent::assertEquals(Base64::fromFile(I::getAlias('@icy2003/php_tests/_data/php.gif')), $this->__getString());
    }

    public function testToFile()
    {
        $file = I::getAlias('@icy2003/php_tests/_data/php2.gif');
        Base64::toFile($this->__getString(), $file);
        parent::assertFileExists($file);
        File::deleteFile($file);
    }
}
